import React from 'react';
import ReactDOM from 'react-dom';

function App() {
  return (
    <div style={{ backgroundColor: '#800080', padding: 20, borderRadius: 10 }}> 
      <h1>AI Chatbot</h1>
      <p>This is a simple chatbot UX.</p>
    </div>
  );
}

ReactDOM.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>,
  document.getElementById('root')
);
